const Discover = ({navigation}) => {
  const [ topAnime, SetTopAnime] = React.useState([]);
  React.useEffect(()=>{
    fetch(`https://api.jikan.moe/v4/seasons/now?limit=7`)
    .then(re => re.json())
    .then((re) => {
      SetTopAnime(re.data);

  })
},[]);



  return (
    <ScrollView style={styles.container}>
      <StatusBar style="light" />
      <SafeAreaView style={{ flex: 1 }}>
      <View style={styles.headerContainer}>
        <Text style={styles.headerTitle}>Airing Now</Text>
        <TouchableOpacity onPress={() => navigation.navigate("Airing Now")}>
          <Text style={styles.headerSubTitle}>VIEW ALL</Text>
        </TouchableOpacity>
      </View>
      <View>
        <FlatList 
          data={topAnime}
          horizontal
          ItemSeparatorComponent={() =><ItemSeparator width={20}/>}
          ListHeaderComponent={() =><ItemSeparator width={20}/>}
          ListFooterComponent={() =><ItemSeparator width={20}/>}
          showsHorizontalScrollIndicator={false}

          keyExtractor={(item) => String(item.mal_id)}
          renderItem={({item}) => (        
            <View style={styles.item}>
            <TouchableOpacity >
                <Image
                    source={{
                        uri: item.images.jpg.image_url,
                    }}
                    style={styles.itemPhoto}
                    resizeMode="cover"
                />
            </TouchableOpacity>
            <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
        </View>

          )}

        />
      </View>
      </SafeAreaView>
    </ScrollView>
  );
};



const styles = StyleSheet.create({
  container: {
    flex: 1,
    //margin:5
    //backgroundColor: '#121212',
  },
  sectionHeader: {
    fontWeight: '800',
    fontSize: 18,
