    return(
        <View>
                    <TouchableOpacity style = {styles.itemPhoto}>
                        <Text style={styles.textStyle}>Anime </Text>
                    </TouchableOpacity>
                <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
                </View>


    )
}

