import ItemSeparator from "../components/ItemSeperator"

const { width,height } = Dimensions.get("screen");

const setWidth = (w) => (width / 100) * w;
const setHeight = (h) => (height / 100) * h;

import { StatusBar } from 'expo-status-bar';
import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  SectionList,
  SafeAreaView,
  Image,
  TouchableOpacity,Dimensions,FlatList,ScrollView
} from 'react-native';


const Discover = ({navigation}) => {
  const [ airingNow, SetAiring] = React.useState([]);
  const [topAnime, SetTopAnime] = React.useState([]);
  const [ upcoming, SetUpcoming] = React.useState([]);
  React.useEffect(()=>{
    fetch(`https://api.jikan.moe/v4/seasons/now?limit=7`)
    .then(re => re.json())
    .then((re) => {
      SetAiring(re.data);

  })
},[]);
React.useEffect(()=>{
  fetch(`https://api.jikan.moe/v4/top/anime?limit=7`)
  .then(re => re.json())
  .then((re) => {
    SetTopAnime(re.data);

})
},[]);
React.useEffect(()=>{
  fetch(`https://api.jikan.moe/v4/seasons/upcoming?limit=7`)
  .then(re => re.json())
  .then((re) => {
    SetUpcoming(re.data);

})
},[]);








  return (
    <ScrollView style={styles.container}>
      <StatusBar style="light" />
      <SafeAreaView style={{ flex: 1 }}>
      <View style={styles.headerContainer}>
        <Text style={styles.headerTitle}>Airing Now</Text>
        <TouchableOpacity onPress={() => navigation.navigate("Airing Now")}>
          <Text style={styles.headerSubTitle}>VIEW ALL</Text>
        </TouchableOpacity>
      </View>
      <View>
        <FlatList 
          data={airingNow}
          horizontal
          ItemSeparatorComponent={() =><ItemSeparator width={20}/>}
          ListHeaderComponent={() =><ItemSeparator width={20}/>}
          ListFooterComponent={() =><ItemSeparator width={20}/>}
          showsHorizontalScrollIndicator={false}

          keyExtractor={(item) => String(item.mal_id)}
          renderItem={({item}) => (        
            <View style={styles.item}>
            <TouchableOpacity >
                <Image
                    source={{
                        uri: item.images.jpg.image_url,
                    }}
                    style={styles.itemPhoto}
                    resizeMode="cover"
                />
            </TouchableOpacity>
            <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
        </View>

          )}

        />
      </View>
      <View style={styles.headerContainer}>
        <Text style={styles.headerTitle}>Trending Anime</Text>
        <TouchableOpacity onPress={() => navigation.navigate("Trending Anime")}>
          <Text style={styles.headerSubTitle}>VIEW ALL</Text>
        </TouchableOpacity>
      </View>
      <View>
        <FlatList 
          data={topAnime}
          horizontal
          ItemSeparatorComponent={() =><ItemSeparator width={20}/>}
          ListHeaderComponent={() =><ItemSeparator width={20}/>}
          ListFooterComponent={() =><ItemSeparator width={20}/>}
          showsHorizontalScrollIndicator={false}
          keyExtractor={(item) => String(item.mal_id)}
          renderItem={({item}) => (        
            <View style={styles.item}>
            <TouchableOpacity >
                <Image
                    source={{
                        uri: item.images.jpg.image_url,
                    }}
                    style={styles.itemPhoto}
                    resizeMode="cover"
                />
            </TouchableOpacity>
            <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
        </View>

          )}

        />
      </View>
      <View style={styles.headerContainer}>
        <Text style={styles.headerTitle}>Upcoming Anime</Text>
        <TouchableOpacity onPress={() => navigation.navigate("Trending Anime")}>
          <Text style={styles.headerSubTitle}>VIEW ALL</Text>
        </TouchableOpacity>
      </View>
      <View>
        <FlatList 
          data={upcoming}
          horizontal
          ItemSeparatorComponent={() =><ItemSeparator width={20}/>}
          ListHeaderComponent={() =><ItemSeparator width={20}/>}
          ListFooterComponent={() =><ItemSeparator width={20}/>}
          showsHorizontalScrollIndicator={false}
          keyExtractor={(item) => String(item.mal_id)}
          renderItem={({item}) => (        
            <View style={styles.item}>
            <TouchableOpacity >
                <Image
                    source={{
                        uri: item.images.jpg.image_url,
                    }}
                    style={styles.itemPhoto}
                    resizeMode="cover"
                />
            </TouchableOpacity>
            <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
        </View>

          )}

        />
      </View>
      <View style={styles.headerContainer}>
        <Text style={styles.sectionHeader}>Trending Anime</Text>
        <TouchableOpacity onPress={() => navigation.navigate("Trending Anime")}>
          <Text style={styles.headerSubTitle}>VIEW ALL</Text>
        </TouchableOpacity>
      </View>
      <View>
        <FlatList 
          data={topAnime}
          horizontal
          ItemSeparatorComponent={() =><ItemSeparator width={20}/>}
          ListHeaderComponent={() =><ItemSeparator width={20}/>}
          ListFooterComponent={() =><ItemSeparator width={20}/>}
          showsHorizontalScrollIndicator={false}
          keyExtractor={(item) => String(item.mal_id)}
          renderItem={({item}) => (        
            <View style={styles.item}>
            <TouchableOpacity >
                <Image
                    source={{
                        uri: item.images.jpg.image_url,
                    }}
                    style={styles.itemPhoto}
                    resizeMode="cover"
                />
            </TouchableOpacity>
            <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
        </View>

          )}

        />
      </View>
      </SafeAreaView>
    </ScrollView>
  );
};



const styles = StyleSheet.create({
  container: {
    flex: 1,

    //margin:5
    //backgroundColor: '#121212',
  },
  sectionHeader: {
    fontWeight: '800',
    fontSize: 18,
    //color: '#f4f4f4',
    marginTop: 20,
    marginBottom: 5,
    //marginStart: 10
  },
    headerSubTitle: {
    fontSize: 12,
    fontWeight: "500",
    color: "#057DFE",
    marginTop: 20,
  },

  //headerTitle: {
  //  fontSize: 20,

  //  fontWeight: "500",
  //  color: "#057DFE",
  //  marginTop: 20,
  //  marginStart: 20,
  //},


  headerContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",




  },

  item: {
    margin: 10,
  },
  itemPhoto: {
    justifyContent: "center",
    alignItems:"center",
    borderRadius: 12,
    backgroundColor: "#057DFE",
    paddingVertical: 8,
    //elevation: 5,
    marginHorizontal:-10,
    marginVertical: 2,
    width:setWidth(35), 
    height:setHeight(25)
  },
  itemText: {
    //color: 'b',
    marginTop: 5,
  },






});

export default Discover;
