import React from "react";
import { Text,View,Button,Alert,TouchableOpacity,ScrollView,StyleSheet,FlatList,Dimensions,Image} from "react-native";
import FlatListBig from "../components/flatlistBig";
import AnimeCard from "../components/animecard";
import ItemSeparator from "../components/ItemSeperator";
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const { width,height } = Dimensions.get("screen");

const setWidth = (w) => (width / 100) * w;
const setHeight = (h) => (height / 100) * h;




const AiringScreen = () => {

    const [ airingNow, SetAiring] = React.useState([]);
    const [topAnime, SetTopAnime] = React.useState([]);
    const [ upcoming, SetUpcoming] = React.useState([]);
    React.useEffect(()=>{
        fetch(`https://api.jikan.moe/v4/seasons/now?limit=7`)
        .then(re => re.json())
        .then((re) => {
          SetAiring(re.data);

      })
    },[]);
    return(
        <ScrollView style={styles.container2}>
        <FlatList  style={styles.flatlist}
          data={airingNow}
          ItemSeparatorComponent={() =><ItemSeparator height={10}width={20}/>}
          ListHeaderComponent={() =><ItemSeparator height={20}/>}
          ListFooterComponent={() =><ItemSeparator height={30}/>}
          showsHorizontalScrollIndicator={false}

          keyExtractor={(item) => String(item.mal_id)}
          renderItem={({item}) => (        
            <View style={styles.item}>
            <TouchableOpacity >
                <Image
                    source={{
                        uri: item.images.jpg.image_url,
                    }}
                    style={styles.itemPhoto}
                    resizeMode="cover"
                />
            </TouchableOpacity>
            <View style={{width: setWidth(30)}}>

                <Text style = {styles.container3}ellipsizeMode='tail' numberOfLines={1}>
                   {item.title}
                </Text>

                </View>
        </View>

          )}
                numColumns={3}
                showsHorizontalScrollIndicator={false}/>

        </ScrollView>

    );
}

export default AiringScreen;


const styles = StyleSheet.create({
    container: {
      flex: 1,
      //margin:5
      //backgroundColor: '#121212',
    },
    container2:{
        //alignContent: "flex-end",
        margin:8,},
    sectionHeader: {
      fontWeight: '800',
      fontSize: 18,
      //color: '#f4f4f4',
      marginTop: 20,
      marginBottom: 5,
      marginStart: 10
    },
      headerSubTitle: {
      fontSize: 12,
      fontWeight: "500",
      color: "#057DFE",
      marginTop: 20,
    },

    headerContainer: {
      flexDirection: "row",
      justifyContent: "space-between",
      alignItems: "center",
      //paddingHorizontal: 20,
      //paddingVertical: 10,
    },

    item: {
      margin: 10,
    },
    itemPhoto: {
        justifyContent: "center",
        alignItems:"center",
        borderRadius: 12,
        backgroundColor: "#057DFE",
        paddingVertical: 8,
        elevation: 5,
        marginHorizontal:4,
        width:setWidth(30), 
        height:setHeight(18)

    },
    itemText: {
      //color: 'b',
      marginTop: 5,
    },

    flatlist: {
        flexDirection: 'column',}
})
